import{_ as a,c as t,o as e,a as p}from"./app-TihkNqhu.js";const n="/assets/P1_C1-2_pic1-C2OaoAGL.jpg",s={},i=p('<h1 id="_1-мобильные-устроиства" tabindex="-1"><a class="header-anchor" href="#_1-мобильные-устроиства"><span>1 МОБИЛЬНЫЕ УСТРОЙСТВА</span></a></h1><h2 id="_1-2-типы-мобильных-устроиств" tabindex="-1"><a class="header-anchor" href="#_1-2-типы-мобильных-устроиств"><span>1.2 Типы мобильных устройств</span></a></h2><p>Под мобильными устройствами понимают ряд цифровых устройств, характерной особенностью которых является компактный размер, перемещаемость и определенный (иногда ограниченный) набор выполняемых функций. В категорию этих устройств относят не только аппараты, позволяющие поддерживать связь посредством технологий сотовой связи, а также игровые консоли, IoT-устройства (устройства интернета вещей), «умные» часы, планшеты и т.д. Для понимания приобретения современными смартфонами их нынешнего вида (и технологических особенностей), рассмотрим некоторых предшественников.</p><p>1 ) Автомобильные радиотелефоны и мобильный радиотелефон – тяжеловесные устройства для обеспечения связи между абонентами. Чтобы поговорить с другим человеком, пользователь звонил на базовую станцию, где оператор подключал его к городской сети. Как и предшественники компьютеров (электронный вычислительные средства), изобретение имело большие габариты по сравнению с нынешними устройствами. Самый легковесный аппарат весил 3 килограмма.</p><p>Изначально радиотелефон работал на мобильной радиосвязи – такой способ, при котором информация передается по радиоволнам. Поддержание связи являлось непростой задачей, поскольку, находящиеся рядом радиоприемники могли терять необходимый канал и, нередко, пользователи могли стать свидетелями чужого разговора.</p><p>Для исключения проблем с потерей и перехватом каналов, был изобретен отдельный вид мобильной радиосвязи – сотовая связь, особенность которой заключается в расположении базовых станций, принимающих и передающих сигналы, в определенных местах. Рисунок покрытия, при проецировании его на карту, зачастую принимает формы сот, от чего данную связь называют «сотовой» (рисунок 1).</p><p><img src="'+n+'" alt="Упрощенное представление работы сотовой связи"></p><p>2 ) Пейджер – считается прародителем современных мобильных устройств. По внешнему виду, он походит на небольшой радиоприемник с функцией получения сообщений. Для отправки сообщения, пользователь звонил в специальную пейджиринговую компанию, диктовал текст и номер получателя для отправки, после чего уже оператор отправлял сообщение с компьютера на пейджер другого пользователя. Обычно данная операция занимала около пяти минут. Пейджер считался прямым конкурентом мобильных телефонов и операторов сотовой связи, поскольку товары и услуги последних в начале 90-х годов стоили много дороже. Однако, популярность пейджеров продержалась недолго. Уже к 2000-му году спрос на них сошел на нет в виду удешевления связи и устройств для ее обеспечения.</p><p>3 ) Мобильное устройство без сим-карты (кирпич) – достаточно громоздкая трубка с антенной. Для получения связи, абонент обращался в специальную компанию, где в аппарат вшивали чип, позволяющий совершать и принимать звонки. Важно заметить, что данный тип устройств выполнял только эти функции, а компании, обслуживающие и предоставляющие чипы являлись единичными филиалами в крупных городах.</p><p>4 ) Мобильное устройство с сим-картами. Абонент сам выбирал провайдера для осуществления звонков. Популярность таких аппаратов связано со следующими пунктами:</p><ul><li>цифровая мобильная связь к этому времени приводится к единому стандарту (GSM). Это говорит о том, что производитель от вопроса обеспечения устройства связью перешел к реализации других улучшенияй, например, уменьшение размеров комплектующих частей;</li><li>изобретение никель-металлогибридных батарей, а после переход на литий-ионные аккумуляторы, позволили значительно уменьшить размеры устройства, продлить срок его работы и уменьшить время на подзарядку;</li><li>относительно малая цена на само устройство и дешевые тарифы на связь.</li></ul><p>С течением времени мобильные телефоны приобрели цветной дисплей, возможность отправки ММS, доступ к мобильному интернету, просмотр мультимедиа, камеру, поддержку карт памяти, встроенные приложения от производителя (телефонная книга, калькулятор, игры) и возможность запуска сторонних приложений (например, java приложения).</p>',12),l=[i];function r(c,o){return e(),t("div",null,l)}const d=a(s,[["render",r],["__file","part1.html.vue"]]),m=JSON.parse('{"path":"/document/part1.html","title":"1 МОБИЛЬНЫЕ УСТРОЙСТВА","lang":"en-US","frontmatter":{},"headers":[{"level":2,"title":"1.2 Типы мобильных устройств","slug":"_1-2-типы-мобильных-устроиств","link":"#_1-2-типы-мобильных-устроиств","children":[]}],"git":{"updatedTime":1719814830000,"contributors":[{"name":"Animakkon","email":"lada.dunskaya@mail.ru","commits":1}]},"filePathRelative":"document/part1.md"}');export{d as comp,m as data};
